import React, { useState } from 'react';

const getStrength = (password) => {
  let score = 0;
  if (password.length > 8) score++;
  if (/[A-Z]/.test(password)) score++;
  if (/[a-z]/.test(password)) score++;
  if (/\d/.test(password)) score++;
  if (/[@$!%*?&]/.test(password)) score++;
  return score;
};

const getStrengthLabel = (score) => {
  if (score >= 4) return 'Strong';
  if (score >= 2) return 'Moderate';
  return 'Weak';
};

const PasswordChecker = () => {
  const [password, setPassword] = useState('');
  const [strength, setStrength] = useState(0);
  const [breachCount, setBreachCount] = useState(null);

 const checkPassword = async (pwd) => {
  setPassword(pwd);

  // Don't proceed if input is empty
  if (!pwd) {
    setStrength(0);
    setBreachCount(null);
    return;
  }

  setStrength(getStrength(pwd));

  const res = await fetch('http://localhost:5000/api/check', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ password: pwd })
  });
  const data = await res.json();
  setBreachCount(data.count);
};

  const barColor =
    strength >= 4 ? 'bg-green-500' : strength >= 2 ? 'bg-yellow-500' : 'bg-red-500';

  return (
    <div className="mb-4 text-left">
      <input
        type="password"
        className="w-full p-2 border border-gray-300 rounded mb-2"
        placeholder="Enter password"
        onChange={(e) => checkPassword(e.target.value)}
        value={password}
      />

      <div className="w-full bg-gray-200 h-3 rounded mb-1">
        <div
          className={`h-3 rounded transition-all duration-300 ${barColor}`}
          style={{ width: `${strength * 20}%` }}
        ></div>
      </div>
      <p className="text-sm mb-2 text-gray-600">
        üîê Password Strength: <strong>{getStrengthLabel(strength)}</strong>
      </p>

      {breachCount !== null && (
        <div className="text-sm mt-2">
          {breachCount > 0 ? (
            <p className="text-yellow-700">
              ‚ö†Ô∏è This password has appeared in <strong>{breachCount}</strong> known data breaches.
              <br />
              üîÑ Please use a unique, stronger password.
            </p>
          ) : (
            <p className="text-green-600">
              ‚úÖ This password has not been found in known data breaches.
            </p>
          )}
        </div>
      )}
    </div>
  );
};

export default PasswordChecker;
