import React, { useState } from 'react';

const getStrength = (password) => {
  let score = 0;
  if (password.length > 8) score++;
  if (/[A-Z]/.test(password)) score++;
  if (/[a-z]/.test(password)) score++;
  if (/\d/.test(password)) score++;
  if (/[@$!%*?&]/.test(password)) score++;
  return score;
};

const getStrengthLabel = (score) => {
  if (score >= 4) return 'Strong';
  if (score >= 2) return 'Moderate';
  return 'Weak';
};

const generateStrongPassword = () => {
  const chars = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789@$!%*?&';
  let password = '';
  for (let i = 0; i < 16; i++) {
    password += chars.charAt(Math.floor(Math.random() * chars.length));
  }
  return password;
};

const PasswordChecker = () => {
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [strength, setStrength] = useState(0);
  const [breachCount, setBreachCount] = useState(null);
  const [generatedPassword, setGeneratedPassword] = useState('');

  const checkPassword = async (pwd) => {
    setPassword(pwd);
    setGeneratedPassword('');

    if (!pwd) {
      setStrength(0);
      setBreachCount(null);
      return;
    }

    setStrength(getStrength(pwd));

    try {
      const res = await fetch('http://localhost:5000/api/check', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ password: pwd }),
      });
      const data = await res.json();
      setBreachCount(data.count);
    } catch (err) {
      console.error('API error:', err);
    }
  };

  const handleGenerate = () => {
    const newPassword = generateStrongPassword();
    setPassword(newPassword);
    setGeneratedPassword(newPassword);
    checkPassword(newPassword);
  };

  const handleDownload = () => {
    const blob = new Blob([password], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'password.txt';
    a.click();
  };

  const barColor =
    strength >= 4 ? 'bg-green-500' : strength >= 2 ? 'bg-yellow-500' : 'bg-red-500';

  return (
    <div className="mb-4 text-left">
      {/* Password Input with Toggle */}
      <div className="relative">
        <input
          type={showPassword ? 'text' : 'password'}
          className="w-full p-2 border border-gray-300 rounded mb-2 pr-12"
          placeholder="Enter password"
          onChange={(e) => checkPassword(e.target.value)}
          value={password}
        />
        <button
          onClick={() => setShowPassword(!showPassword)}
          className="absolute right-2 top-2 text-sm text-blue-500"
        >
          {showPassword ? 'üôà Hide' : 'üëÅÔ∏è Show'}
        </button>
      </div>

      {/* Password Meter */}
      {password && (
        <>
          <div className="w-full bg-gray-200 h-3 rounded mb-1">
            <div
              className={`h-3 rounded transition-all duration-300 ${barColor}`}
              style={{ width: `${strength * 20}%` }}
            ></div>
          </div>
          <p className="text-sm mb-2 text-gray-600">
            üîê Password Strength: <strong>{getStrengthLabel(strength)}</strong>
          </p>
        </>
      )}

      {/* Live Suggestions */}
      {password && strength < 4 && (
        <ul className="text-sm text-red-500 mb-2 ml-4 list-disc">
          {password.length <= 8 && <li>Use at least 8 characters</li>}
          {!/[A-Z]/.test(password) && <li>Include uppercase letters</li>}
          {!/[a-z]/.test(password) && <li>Include lowercase letters</li>}
          {!/\d/.test(password) && <li>Add numbers</li>}
          {!/[@$!%*?&]/.test(password) && <li>Add special characters</li>}
        </ul>
      )}

      {/* Breach Count */}
      {password && breachCount !== null && (
        <div className="text-sm mt-2">
          {breachCount > 0 ? (
            <p className="text-yellow-700">
              ‚ö†Ô∏è This password appeared in <strong>{breachCount}</strong> breaches. Please avoid using it.
            </p>
          ) : (
            <p className="text-green-600">‚úÖ This password has not been found in breaches.</p>
          )}
        </div>
      )}

      {/* Action Buttons */}
      <div className="flex flex-wrap gap-3 mt-4">
        <button
          onClick={handleGenerate}
          className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700 transition"
        >
          üé≤ Generate Password
        </button>
        <button
          onClick={() => navigator.clipboard.writeText(password)}
          className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300 transition"
        >
          üìã Copy
        </button>
        <button
          onClick={handleDownload}
          className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300 transition"
        >
          ‚¨áÔ∏è Download
        </button>
      </div>

      {/* Show Generated Password */}
      {generatedPassword && (
        <div className="mt-4 p-3 bg-gray-100 rounded border text-sm text-gray-700">
          üîê Generated Password: <code className="font-mono break-all">{generatedPassword}</code>
        </div>
      )}
    </div>
  );
};

export default PasswordChecker;
