import React from 'react';
import PasswordChecker from './components/PasswordChecker';
import Generator from './components/Generator';

function App() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 to-white p-6 text-gray-800">
      <div className="max-w-4xl mx-auto grid gap-8 md:grid-cols-2">

        {/* Section 1: Strength Checker */}
        <section className="bg-white shadow-xl rounded-xl p-6 md:p-8">
          <h2 className="text-2xl font-bold text-indigo-700 mb-3">üîê Password Strength Checker</h2>
          <p className="text-gray-600 mb-4">
            Check if your password is strong and whether it's been found in any data breaches.
          </p>
          <PasswordChecker />
        </section>

        {/* Section 2: Password Generator */}
        <section className="bg-white shadow-xl rounded-xl p-6 md:p-8">
          <h2 className="text-2xl font-bold text-indigo-700 mb-3">üé≤ Secure Password Generator</h2>
          <p className="text-gray-600 mb-4">
            Create strong, secure passwords tailored to your preferences.
          </p>
          <Generator />
        </section>
      </div>
    </div>
  );
}

export default App;
