import React, { useState } from 'react';

const generatePassword = (length = 16) => {
  const charset = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789@#$!%*?&';
  let password = '';
  for (let i = 0; i < length; i++) {
    const randomIndex = Math.floor(Math.random() * charset.length);
    password += charset[randomIndex];
  }
  return password;
};

const Generator = () => {
  const [generatedPassword, setGeneratedPassword] = useState('');
  const [copied, setCopied] = useState(false);

  const handleGenerate = () => {
    const newPassword = generatePassword();
    setGeneratedPassword(newPassword);
    setCopied(false);
  };

  const handleCopy = () => {
    if (generatedPassword) {
      navigator.clipboard.writeText(generatedPassword);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    }
  };

  return (
    <div className="mt-6 text-left">
      <button
        onClick={handleGenerate}
        className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700 transition"
      >
        üîê Generate Strong Password
      </button>

      {generatedPassword && (
        <div className="mt-4 bg-gray-100 border rounded p-3 text-sm">
          <div className="flex items-center justify-between">
            <span className="break-all font-mono text-gray-800">{generatedPassword}</span>
            <button
              onClick={handleCopy}
              className="ml-4 text-indigo-600 hover:text-indigo-800 transition"
            >
              üìã {copied ? 'Copied!' : 'Copy'}
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Generator;
