import React, { useState, useEffect } from 'react';
import PasswordChecker from './components/PasswordChecker';
import Generator from './components/Generator';

function App() {
  const [darkMode, setDarkMode] = useState(false);

  // Toggle `dark` class on <html> for Tailwind dark mode
  useEffect(() => {
    document.documentElement.classList.toggle('dark', darkMode);
  }, [darkMode]);

  return (
    <div className={`min-h-screen flex flex-col items-center justify-center px-4 py-8 transition-all duration-500 ${darkMode ? 'bg-gray-900' : 'bg-indigo-100'}`}>

      {/* Toggle Button */}
      <button
        onClick={() => setDarkMode(!darkMode)}
        className="absolute top-4 right-4 bg-gray-200 dark:bg-gray-700 text-sm px-3 py-1 rounded shadow hover:scale-105 transition"
      >
        {darkMode ? '‚òÄÔ∏è' : 'üåô '}
      </button>

      {/* Main Card */}
      <div className="bg-white/50 dark:bg-gray-800/40 backdrop-blur-md shadow-2xl border border-gray-200 dark:border-gray-700 rounded-2xl p-8 w-full max-w-xl text-center transition-all duration-300">

        {/* Title */}
        <h1 className="text-3xl font-bold text-indigo-700 dark:text-indigo-300 mb-4">üîê SafePass</h1>
        <p className="mb-6 text-gray-600 dark:text-gray-300">Check your password strength & see if it‚Äôs been in a data breach.</p>

        {/* Components */}
        <PasswordChecker />
        <Generator />
      </div>
    </div>
  );
}

export default App;
